                    </div>
                    <div className='map-container'>
                        <div className='map-info'>
                            <h1>{this.state.name}</h1>
                            <h3>{this.state.title}</h3>
                        </div>
                        <MapContainer
                            updateLocationPanel={this.updateLocationPanel}
                        />
                    </div> 
                </div>
            );
