import React, { Component } from 'react';
import './resume.css';
import Contacts from './contacts/contacts'
import Education from './education/education'
import Interships from './internships/internships'
import Skills from './skills/skills'

class Resume extends Component {
    constructor(){
        super();
        this.state = {
            showContacts: false,
            showEducation: false,
            showInternships: false,
            showSkills: false,
            resumeCategories: [
                {name: 'Contacts', func: this.toggleContactVisibility, bool: this.showContacts},
                {name: 'Education', func: this.toggleEducationVisibility, bool: this.showEducation},
                {name: 'Intenships', func: this.toggleInternshipVisibility, bool: this.showInternships},
                {name: 'Skills', func: this.toggleSkillsVisibility, bool: this.showSkills},
            ],
        }
    }

    // toggleVisibility()

componentDidMount(){
    // fetch('/api/customer')
    // .then(res => res.json())
    // .then(customers => this.setState({customers},
    //     () => console.log('Customers fetched...', customers)))
}

    render() {
        return (
            <div>
                <div className='resume-categories'>
                    <div className='menu-tab-div'>
                    <button className='menu-tab-button' onClick={this.toggleVisibility}>Contacts</button>
                    <img className={this.state.showContacts && 'open-menu-arrow'} src='/images/arrow.png' height='20' width='20' alt='arrow'/>
                    </div>
                    <div className='menu-tab-div'>
                    <button className='menu-tab-button' onClick={this.toggleVisibility}>Contacts</button>
                    <img className={this.state.showEducation && 'open-menu-arrow'} src='/images/arrow.png' height='20' width='20' alt='arrow'/>
                    </div>
                    <div className='menu-tab-div'>
                    <button className='menu-tab-button' onClick={this.toggleVisibility}>Contacts</button>
                    <img className={this.state.showInternships && 'open-menu-arrow'} src='/images/arrow.png' height='20' width='20' alt='arrow'/>
                    </div>
                    <div className='menu-tab-div'>
                    <button className='menu-tab-button' onClick={this.toggleVisibility}>Contacts</button>
                    <img className={this.state.showSkills && 'open-menu-arrow'} src='/images/arrow.png' height='20' width='20' alt='arrow'/>
                    </div>
                </div>
                <div>                                         
                {this.state.showContacts &&
                    <Contacts/>
                }
                {this.state.showEducation &&
                    <Education/>
                }
                {this.state.showInternships &&
                    <Interships/>
                }
                {this.state.showSkills &&
                    <Skills/>
                }
                </div>
            </div>
        );
    }
}

export default Resume;
