import React, { Component } from 'react';
import { VerticalBarSeries, CircularGridLines, LabelSeries, RadarChart } from 'react-vis';
import './graph_styling.css'

class Genre_Analysis extends Component {
    constructor(props) {
        super(props);
        this.state = {
            index: null,
        }
    }
    render() {
        const dataWithColor = this.props.genreData.map((d, i) => ({...d, color: Number(i !== index)}));
        const artistsWithFocus = this.props.top_5_artists_images.map((d, i) => ({...d, highlight: i === index}));
        const labelData = data2.map((d, idx) => ({
            x: d.x,
            y: d.y
        }));
        return (
            <div>
                <XYPlot
          xType="ordinal"
          width={300}
          height={300}
          // https://codesandbox.io/s/bar-series-that-change-colors-on-mouseover-d9zh8
          onMouseLeave={() => this.setState({ index: null, artistsWithFocus: this.props.top_5_artists_images })}
        >
          <VerticalBarSeries
            data={dataWithColor}    
            animation={{damping: 9, stiffness: 300}}    
            onNearestX={(d, { index }) => this.setState({ index, artistsWithFocus })}
          />
          <LabelSeries 
              data={labelData}
              allowOffsetToBeReversed
              getLabel={d => d.y}/>
        </XYPlot>
            </div>
        );
    }
}

export default Genre_Analysis;
