import React, { Component } from 'react';
import './resume.css';
import Contacts from './contacts/contacts'
import Education from './education/education'
import Interships from './internships/internships'
import Skills from './skills/skills'

class Resume extends Component {
    constructor(){
        super();
        this.state = {
            showContacts: false,
            showEducation: false,
            showInternships: false,
            showSkills: false,
        }
        this.toggleContactVisibility = this.toggleContactVisibility.bind(this);
        this.toggleEducationVisibility = this.toggleEducationVisibility.bind(this);
        this.toggleInternshipsVisibility = this.toggleInternshipsVisibility.bind(this);
        this.toggleSkillsVisibility = this.toggleSkillsVisibility.bind(this);
    }
    toggleContactVisibility() {
        this.setState(state => ({
            showContacts: !state.showContacts,
        }));
    }
    toggleEducationVisibility() {
        this.setState(state => ({
            showEducation: !state.showEducation,
        }));
    }
    toggleInternshipsVisibility() {
        this.setState(state => ({
            showInternships: !state.showInternships,
        }));
    }
    toggleSkillsVisibility() {
        this.setState(state => ({
            showSkills: !state.showSkills,
        }));
    }

    componentDidMount(){
        // fetch('/api/customer')
        // .then(res => res.json())
        // .then(customers => this.setState({customers},
        //     () => console.log('Customers fetched...', customers)))
    }

    render() {
        const resumeCategories = [
            {name: 'Contacts', func: this.toggleContactVisibility, bool: this.showContacts},
            {name: 'Education', func: this.toggleEducationVisibility, bool: this.showEducation},
            {name: 'Intenships', func: this.toggleInternshipVisibility, bool: this.showInternships},
            {name: 'Skills', func: this.toggleSkillsVisibility, bool: this.showSkills},
        ];
        return (
            <div>
                <div className='resume-categories'>
                    {resumeCategories.map(cat =>
                        <div className='menu-tab-div'>
                        <button className='menu-tab-button' onClick={cat.func}>{cat.name}</button>
                        <img className={cat.bool && 'open-menu-arrow'} src='/images/arrow.png' height='20' width='20' alt='arrow'/>
                        </div>)}
                </div>
                <div>                                         
                {this.state.showContacts &&
                    <Contacts/>
                }
                {this.state.showEducation &&
                    <Education/>
                }
                {this.state.showInternships &&
                    <Interships/>
                }
                {this.state.showSkills &&
                    <Skills/>
                }
                </div>
            </div>
        );
    }
}

export default Resume;
