        }
      ]
    },
    {
      "featureType": "transit.line",
      "elementType": "geometry",
      "stylers": [
        {
          "color": "#e5e5e5"
        }
      ]
    },
    {
      "featureType": "transit.station",
      "elementType": "geometry",
      "stylers": [
        {
          "color": "#eeeeee"
        }
      ]
    },
    {
      "featureType": "water",
      "elementType": "geometry",
      "stylers": [
        {
          "color": "#c9c9c9"
        }
      ]
    },
    {
      "featureType": "water",
      "elementType": "labels.text.fill",
      "stylers": [
        {
          "color": "#9e9e9e"
        }
      ]
    }
  ];



export class MapContainer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            showingInfoWindow: false,
            activeMarker: {},
            selectedPlace: {},
        };
    }
    onMarkerHover = (prop, marker, e) => {
        if(this.state.selectedPlace.name !== prop.name) {
            this.setState({
            selectedPlace: prop,
            activeMarker: marker,
            showingInfoWindow: true
            });
            this.props.updateLocationPanel(prop, marker);
        }
    }   

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
        this.setState({
            showingInfoWindow: false,
            activeMarker: null
        })
        }
    };


  render() {
    const google=this.props.google
    return (
      <Map 
        google={google} 
        zoom={3}
        initialCenter={{
            lat: 39.8283,
            lng: -98.5795
          }}
        style={style2}
        >
        <Marker
            onMouseover={this.onMarkerHover}
            title={'The marker`s title will appear as a tooltip.'}
            name={'Visual Labs Inc.'}
            position={{lat: 37.4528649, lng: -122.1829978}} 
            icon={{
                url: "/images/visuallabs.png",
                anchor: new google.maps.Point(32,32),
                scaledSize: new google.maps.Size(32,32)
            }} 
        />
        <Marker
            onMouseover={this.onMarkerHover}
            title={'The marker`s title will appear as a tooltip.'}
            name={'University of Southern California'}
            position={{lat: 34.0224, lng: -118.2851}} 
            icon={{
                url: "/images/usc.png",
                anchor: new google.maps.Point(32,32),
                scaledSize: new google.maps.Size(32,32)
            }} 
        />
        <Marker
            onMouseover={this.onMarkerHover}
            title={'The marker`s title will appear as a tooltip.'}
            name={'Northeastern University'}
            position={{lat: 42.3398, lng: -71.0892}} 
            icon={{
                url: "/images/neu.png",
                anchor: new google.maps.Point(32,32),
                scaledSize: new google.maps.Size(32,32)
            }} 
        />
        <Marker
            onMouseover={this.onMarkerHover}
            title={'The marker`s title will appear as a tooltip.'}
            name={'Harvard-Westlake High School'}
            position={{lat: 34.0887, lng: -118.4340}} 
            icon={{
                url: "/images/hw.png",
                anchor: new google.maps.Point(32,32),
                scaledSize: new google.maps.Size(32,32)
            }} 
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}>
            <div>
                <h1>{this.state.selectedPlace.name}</h1>
              <h4>{this.state.selectedPlace.title}</h4>
            </div>
        </InfoWindow>
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: ('AIzaSyB4ZrcYecpeQwsvLaRxrnM4IFbI09P4jPA')
})(MapContainer)
